import React from 'react';
import {
  StyleSheet,
  Image,
  Text,
  View,
  ImageBackground,
  TouchableOpacity,
} from 'react-native';
import { Icon } from 'react-native-elements';
import Header from '../components/Header.js';
import theme, { colors } from '../style.js';

export default function Announcements() {
  return (
    <View style={styles.Container}>
      <Header title={'Announcements'} />
      <View style={styles.List}>
        <View style={styles.Card}>
          <TouchableOpacity>
            <View style={styles.Frame6}>
              <Text style={[theme.Title, styles.Title]}>
                Donna Regali needs your help
              </Text>
              <Text style={theme.base}>
                Saint Bernard Hospital, Angel Street 21
              </Text>
            </View>
            <View style={styles.Frame7}>
              <Text style={[theme.SmallText, styles.Info]}>
                Short Information
              </Text>
              <Text style={theme.base}>
                Donna needs donations for AB blood type. She has been in a car
                accident and has three previous incidents.
              </Text>
            </View>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  Container: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    paddingTop: 31,
    paddingBottom: 15,
    paddingLeft: '5%',
    paddingRight: '5%',
    width: '100%',
    // height: 464,
    backgroundColor: '#fff',
  },
  List: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    paddingLeft: '5%',
    paddingRight: '5%',
    width: '100%',
  },

  FriendsRow: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    paddingTop: 0,
    paddingBottom: 3,
    paddingLeft: 0,
    paddingRight: 0,
  },
  Friend: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    flex: 1,
    paddingTop: 20,
    paddingBottom: 20,
    paddingLeft: 32,
    paddingRight: 32,
    borderRadius: 12,
    backgroundColor: 'rgba(255, 255, 255, 1)',
    shadowColor: 'rgba(0,0,0,0.12)',
    elevation: 1,
    shadowOffset: { width: 0, height: 2 },
    // width: 358,
  },
  Card: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    // shadowColor: colors.black,
    // shadowOffset: { width: 0, height: 2 },
    // shadowOpacity: 0.2,
    // shadowRadius: 2,
    borderRadius: 12,
    padding: 16,
    borderColor: 'rgba(60, 60, 67, 0.1)',
    borderWidth: 1,
  },

  Frame6: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    marginBottom: 12,
  },

  Info: {
    fontWeight: '700',
    marginBottom: 4,
  },

  Title: {
    marginBottom: 8,
  },
});
