import { Icon } from 'react-native-elements';
import { View, Text, StyleSheet } from 'react-native';
import theme, { colors } from '../style.js';
import { useNavigation } from '@react-navigation/native';

const Announcement = (props) => {
  const { item } = props.route.params;
  return (
    <View style={styles.Info} {...props}>
      <View style={styles.Frame6}>
        <Text style={[theme.Title, styles.Title]}>{item.title}</Text>
        <Text style={theme.base}>{item.location}</Text>
      </View>
      <View style={styles.Frame6}>
        <Text style={[theme.ButtonText, styles.Info]}>Short Information</Text>
        <Text style={theme.base}>{item.info}</Text>
      </View>
    </View>
  );
};

export default Announcement;

const styles = StyleSheet.create({
  Info: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    padding: '5%',
    width: '100%',
    backgroundColor: '#fff',
    height: '100%',
  },
  PageTitle: {
    // flex: 1,
    textAlign: 'center',
  },
  Info: {
    fontWeight: '700',
    marginBottom: 4,
  },

  Title: {
    marginBottom: 8,
  },

  Frame6: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'flex-start',
    marginBottom: 12,
  },
});
