import * as React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import SimpleInput from '../components/SimpleInput';

function AddAnnouncement() {
  const handleAdd = (values) => {
    const { title, location, shortInfo, fullInfo, rh, bloodType } = values;
    // signInWithEmailAndPassword(auth, email, password).then((response) => {
    //   setUser(response);
    //   navigation.navigate('Announcements');
    // });
  };
  return (
    <View style={styles.Container}>
      <View style={styles.Form}>
        <Formik initialValues={{ title: '' }} onSubmit={handleAdd}>
          {({ handleChange, handleBlur, handleSubmit, values }) => (
            <>
              <View style={styles.Form}>
                <Input
                  placeholder={'Your email address'}
                  label='Address'
                  onChangeText={handleChange('email')}
                  onBlur={handleBlur('email')}
                  value={values.email}
                />
                <Input
                  placeholder={'Password'}
                  label='Password'
                  onChangeText={handleChange('password')}
                  onBlur={handleBlur('password')}
                  value={values.password}
                  secureTextEntry
                />
              </View>
              <View style={styles.Welcome}>
                <CustomButton title={'Login'} onPress={handleSubmit} />
                <View style={[styles.RegisterOption]}>
                  <Text style={theme.Base}>Don't have an account? </Text>
                  <View styles={styles.RegisterButton}>
                    <Text
                      style={[theme.Link, styles.RegisterLink]}
                      onPress={() => navigation.navigate('Register')}
                    >
                      Register now
                    </Text>
                    <Image
                      style={{
                        width: '100%',
                        resizeMode: 'contain',
                        height: 10,
                      }}
                      source={require('../assets/image.png')}
                    />
                  </View>
                </View>
              </View>
            </>
          )}
        </Formik>
        <SimpleInput placeholder={'Title'} />
        <SimpleInput placeholder={'Short information'} />
        <SimpleInput placeholder={'Location'} />
        <SimpleInput
          placeholder={'Full Information'}
          editable
          maxLength={400}
        />
      </View>
    </View>
  );
}

export default AddAnnouncement;

const styles = StyleSheet.create({
  Container: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'center',
    flex: 1,
    paddingTop: 63,
    paddingBottom: 63,
    backgroundColor: 'rgba(255, 255, 255, 1)',
    height: 728,
    width: '100%',
  },
  Welcome: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 40,
    paddingLeft: '5%',
    paddingRight: '5%',
    width: '100%',
  },
  RegisterOption: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 24,
  },
  RegisterButton: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'flex-start',
  },
  RegisterLink: {
    paddingTop: 10,
  },
  Form: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    paddingLeft: '5%',
    paddingRight: '5%',
    width: '100%',
  },
  Txt145: {
    color: 'rgba(0,0,0,1)',
    textAlign: 'center',
    justifyContent: 'center',
    width: '100%',
    marginBottom: 20,
  },

  Txt264: {
    letterSpacing: -0.2,
    color: 'rgba(0,0,0,1)',
    textAlign: 'center',
    justifyContent: 'center',
    width: '90%',
  },

  Txt383: {
    color: 'rgba(11,10,10,1)',
    textAlign: 'center',
    justifyContent: 'center',
    width: '100%',
  },

  Txt242: {
    color: 'rgba(243,243,243,1)',
    textAlign: 'center',
    justifyContent: 'center',
    width: '100%',
  },
});
